UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"清洗机清洗架升降台车","description":"清洗架转运台车 – 专为运输清洗架和进行离线清洗效果测试而设计。","introduction":"该设备由316L不锈钢制成，采用316L卫生管道和轨道焊接。配备SKF升降柱，全电解抛光表面处理，由24V直流电池供电。","features":["升降范围：580 mm – 980 mm","最大承重：400 kg","可充电锂电池","遥控升降","离线清洗效果测试","电解抛光处理","可针对特定设备占地面积进行定制"],"application_scenarios":["清洗架转运","清洗物品的清洗效果评估"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["TIG焊接","无缝焊接","电解抛光"]}}}'::jsonb)
