UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"清洗机清洗架","description":"带集成管道和喷嘴的清洗架，专为特定装载物品定制设计的清洗架。","introduction":"整个清洗架由316L不锈钢制成，采用316L卫生管路系统。所有管道均按卫生标准焊接，确保内部和外部焊缝光滑一致。该装置采用PEEK喷嘴，设计用于匹配清洗机的工作水压。该清洗架可以通过转运台车运入清洗机，连接到机器的内部管道系统以清洗装载的零件。它还支持停靠在转运台车上进行离线清洗效果验证。整个清洗架采用电解抛光表面处理。","features":["结构材料：整个清洗架结构由316L不锈钢制成","卫生管路系统：由316L卫生管道制成。按卫生标准焊接，确保内部和外部接缝光滑一致。","高性能喷嘴：配备耐用的PEEK喷嘴，具有最佳的耐化学性和喷雾模式。","压力兼容性：设计用于匹配所连接清洗机的特定工作水压。","机内清洗：通过台车无缝输送到清洗机中，并连接到机器的内部供水系统。","离线验证：支持停靠在转运台车上在机器外部进行清洗效果测试。","表面处理：全电解抛光，具有卓越的卫生性和耐腐蚀性。"],"application_scenarios":["设施清洁","泄漏响应","日常消毒"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"316L 不锈钢"},"process_technology":{"value":["电解抛光"]}}}'::jsonb)
WHERE id = '74063c9b-5521-4102-9de5-955a6e493195';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"传递窗","description":"带机械互锁的静态传递窗。双层壁不锈钢结构防止洁净区之间的交叉污染。","introduction":"从源头控制污染。我们的传递窗作为不同洁净度级别区域之间的屏障。坚固的机械互锁系统确保一扇门在另一扇门打开时保持锁定，防止空气直接交换。内腔采用圆角（圆弧半径）以消除微粒陷阱并允许彻底消毒。","features":["材料选项：304L/316L 不锈钢","内部不锈钢框架增加支撑","视窗","互锁和/或自闭门"],"application_scenarios":["物料传递","样品传递","药房配药","酸、碱或溶剂化学品传递和储存"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["拉丝处理","无缝焊接"]}}}'::jsonb)
WHERE id = '0d6b57a7-89d4-4871-a9ba-95d78a8c7bfa';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"洁净室电脑工作站","description":"嵌入式不锈钢电脑外壳，带键盘托盘。在冲洗和无菌环境中保护IT硬件。","introduction":"在不牺牲洁净度的情况下集成数字工作流程。这款嵌入式PC外壳设计用于将标准显示器和键盘容纳在洁净室墙壁内，消除了积聚灰尘的水平表面。密封单元具有用于显示器访问的铰链玻璃门和折叠或集成键盘站，全部由耐腐蚀的304不锈钢制成。","features":["嵌入式设计，实现无缝墙壁集成","钢化玻璃屏幕保护","集成键盘架/隔间","密封电缆入口点","耐生物和化学试剂","通风用于设备冷却"],"application_scenarios":["HMI终端","洁净室数据输入","数字SOP站"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["精密激光切割","拉丝处理","TIG焊接","无缝焊接"]}}}'::jsonb)
WHERE id = '58c3e8ac-63c4-42bd-bde3-86f74feb74de';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"多层台车","description":"洁净室多层台车是一种高性能运输解决方案，由无腐蚀、不脱落材料（通常为304或316L不锈钢）制成。","introduction":"专为无菌或微粒控制环境设计的高级移动存储和运输解决方案。其主要目的是在不同区域（例如从更衣室到A级实验室）之间移动精密仪器、化学品或无菌产品，而不会引入“生物负载”或金属粉尘。这些台车专注于不脱落材料和易于清洁的表面，对于优化工作流程效率同时遵守ISO分类标准至关重要。","features":["材料结构：通常由304或316L级不锈钢制成。这对异丙醇（IPA）或过氧化氢等强效消毒剂具有卓越的耐腐蚀性。","电解抛光处理：许多型号采用电解抛光表面，可降低微观表面粗糙度，确细菌无处藏身。","ESD（静电放电）安全：对于电子制造，推车配备导电轮和接地链，以防止静电积聚损坏敏感组件。","无痕脚轮：车轮通常由优质聚氨酯或合成橡胶制成，不会在环氧洁净室地板上留下磨损痕迹。","人体工程学设计：管状手柄无缝焊接（非螺栓连接），消除可积聚灰尘的“死角”。","穿孔 vs. 实心搁板：* 实心：防止滴落物到达下层。\n* 穿孔：允许层流通过推车，保持洁净室气压平衡。"],"application_scenarios":["制药：移动小瓶、注射器或消毒玻璃器皿。","半导体：在FOUP中运输硅晶圆。","医院：在手术室内移动无菌手术包。","研究实验室：在不同封闭区之间组织化学品和样品。"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["拉丝处理","无缝焊接"]}}}'::jsonb)
WHERE id = '396c0656-c1c3-4114-8f3e-bf1971c4a1d2';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"存储架","description":"一种模块化、高稳定性搁板单元，由不脱落材料制成，设计用于在无菌或微粒敏感区域实现最大气流和易于去污。","introduction":"在洁净室中，“标准”搁板是一种负担。产生微粒的油漆、隐藏的锈迹和积聚灰尘的平坦表面可能会毁掉一批生产。洁净室存储架通过使用电解抛光不锈钢解决这个问题，并且通常采用金属丝网或穿孔设计。这些专门的结构确保垂直层流气流（来自天花板的“洁净空气淋浴”）可以通过机架而不是被阻挡，从而防止污染物可能沉降的“死区”。","features":["开放式线材或穿孔设计：最大限度减少积尘表面积，并允许畅通无阻的垂直气流。","不脱落材料：通常由304或316L不锈钢制成，不会剥落、碎裂或脱气。","可调且模块化：无需可能产生金属屑或碎片的专用工具即可重新定位搁板。","高承载能力：专为高重量强度比设计，可支撑重型实验室设备或散装化学品容器。","ESD兼容性：天然导电性能（通常与接地链配对），保护敏感电子设备免受静电放电影响。"],"application_scenarios":["WIP（在制品）分期：在加工阶段之间放置半成品半导体晶圆或医疗设备。","无菌供应存储：在医院或制药准备室整理高压灭菌工具和手术包。","冷却/干燥架：允许新清洗的组件在HEPA过滤流下风干。","化学品管理：以可见、有组织的方式存储非反应性罐和试剂。","洁净室鞋/木底鞋存储：用于更衣区，使专用鞋类离地并有序。","电子外壳：在测试和组装过程中放置电路板架或敏感硬件。"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["无缝焊接","双层覆层","拉丝处理"]}}}'::jsonb)
WHERE id = 'f8286995-a448-4d40-bb3b-8b93eb910a3c';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"冰箱台车","description":"一种高级不锈钢运输系统，设计用于在洁净室环境中移动温度敏感的生物制剂、化学品或样品。它结合了ISO级清洁度兼容性与集成冷却或绝热存储，以保持特定的热条件。","introduction":"在无菌环境中，运输的“最后一英里”——将产品从长期医用冰箱移动到加工台——是一个关键的脆弱环节。洁净室冰箱台车弥补了这一差距。这些台车主要由304或316L不锈钢制成，不脱落、耐腐蚀，并设计有光滑表面，以防止微粒和细菌积聚。","features":["无菌结构：全焊接不锈钢框架，采用拉丝或电解抛光处理，可承受腐蚀性VHP（汽化过氧化氢）或酒精基清洁剂。","先进的移动性：配备无痕、ESD安全（静电耗散）车轮，防止静电积聚并确保地板完整性。","温度管理：具有主动制冷（电池供电）或被动冷却（带冷板槽的高密度绝热）。","人体工程学设计：平滑、连续焊接手柄和“无钩挂”角落，符合安全和卫生协议。","数字监控：通常包括用于实时温度记录和偏差报警系统的集成LED显示屏。"],"application_scenarios":["制药：在洁净区之间运输疫苗、胰岛素或活性药物成分（API）。","生物技术：移动需要在严格的2°C至8°C环境中保存的活细胞培养物、酶或组织样本。","医学研究：将血浆或敏感试剂从冷藏库转移到测试站。","半导体：处理对微粒和热量都敏感的专用冷藏化学品或光刻胶。"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["激光切割","无缝焊接","拉丝处理"]}}}'::jsonb)
WHERE id = '218fcd5f-32fa-46f2-8724-470efb629bfa';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"更衣柜","description":"一种高性能存储单元，通常由不锈钢制成，设计用于在无菌、有组织且通常经过HEPA过滤的环境中存储洁净室服装，以防止灰尘和微生物在服装上积聚。","introduction":"更衣柜是“更衣室”或“缓冲间”的主要设备。其主要目的是作为外部世界与无菌生产区之间的缓冲区。通过为悬挂1或搁置洁净室服装提供专用空间，这些柜子最大限度地减少了以服装的处理和暴露于环境空气中，从而显著延长了服装的寿命和无菌性。","features":["空气过滤（可选但常见）：集成HEPA或ULPA过滤器，在服装上提供连续的层流空气（100级/ ISO 5），吹走任何沉降的微粒。","材料完整性：由304或316L不锈钢制成，易于擦拭并耐受灭菌化学品。","倾斜顶部：倾斜的屋顶设计，防止灰尘积聚，并阻止在单元顶部放置物品。","静电耗散：通常采用ESD安全玻璃或丙烯酸门，以防止吸附灰尘的静电积聚。","可配置内部：包括挂衣杆、用于折叠物品的可调节搁板以及用于“洁净室鞋”或“木底鞋”的专用隔间。"],"application_scenarios":["无菌分期：保持制药灌装套件中使用的长袍和头罩的无菌性。","防静电：存储用于半导体和微电子组装的ESD安全“连体服”。","污染控制：保护航空航天和高精密光学实验室中使用的低皮棉服装。","跨区缓冲：分隔不同生物安全级别（BSL）中使用的服装，以防止交叉污染。","手术完整性：为超洁净医院手术室存放专用鞋类和头饰。","访客管理：为客人或审核员使用的临时长袍提供无菌存储点。"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["拉丝处理","无缝焊接","激光切割"]}}}'::jsonb)
WHERE id = '4fdf2d5a-f5d8-402d-84f5-076b3689a42c';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"氮气柜","description":"一种密封存储系统，用加压氮气置换内部大气，以实现近零湿度（通常 <1% 至 10% RH）和惰性、非反应性环境，用于高价值敏感组件。","introduction":"在洁净室操作中，水分和氧气是“隐形污染物”。氮气柜通过“吹扫”解决这个问题——强制氮气进入柜体，通过单向泄压阀排出较重的潮湿空气。由于氮气是惰性气体，它在材料周围形成稳定的“保护层”，使其成为长期存储半导体、光学器件和反应性化学品的黄金标准。","features":["自动吹扫控制：使用RH（相对湿度）传感器仅在湿度上升时（例如开门后）触发N2流动，显著减少气体消耗。","气密结构：由304/316L不锈钢或静电耗散PVC制成，带有加固门框和无脱气垫圈。","气室分配：带有穿孔的内部“气室”墙壁可确保气体均匀分布在所有搁板上，防止“湿气袋”。","压力管理：集成流量计和泄压阀可防止过压，同时保持对外部灰尘的正压密封。","快速恢复：能够在关门后几分钟内恢复到目标湿度水平（例如 <5% RH），比基于干燥剂的系统快得多。"],"application_scenarios":["晶圆和芯片存储：防止硅晶圆、引线框架和微芯片在制造步骤之间氧化。","MSD保护：安全存放潮湿敏感器件（MSD），防止后续焊料回流过程中出现“爆米花效应”（开裂）。","光学保存：存储高精度透镜和激光晶体，防止因湿度导致表面起雾和“玻璃病”。","生物技术稳定性：保护在氧气存在下降解的吸湿性（吸湿）粉末、DNA样本和酶。","航空航天电子：长期保存关键航空电子设备和传感器，使其多年保持“出厂新鲜”状态。","博物馆和档案馆：保存稀有文物或敏感胶片，否则会因氧化老化而变质。"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢 / SD-PVC"},"process_technology":{"value":["拉丝处理","激光切割","无缝焊接"]}}}'::jsonb)
WHERE id = '41421da7-30c7-4318-bcb2-b4681caf6b25';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"水池","description":"精密设计的不锈钢卫生站，采用无缝、无缝隙设计和免提操作，以保持受控环境中严格的卫生协议。","introduction":"洁净室水池是无菌区入口或出口处防止交叉污染的主要防线。标准水池有用细菌滋生的接缝和溢流口；洁净室水池通过整体结构消除了这些风险。每条曲线都经过圆角（内凹）处理，易于擦拭，并且硬件通常是自动化的，确保用户永远不必接触手柄，保持手部卫生。","features":["无缝结构：由304或316L不锈钢制成，带有全焊接圆角，消除生物膜可生长的裂缝。","免提操作：配备红外传感器、膝盖操作面板或脚踏板，防止洗手后再次污染手部。","卫生排水：具有专门的“后置”或“锥形”排水口，防止积水并最大限度减少飞溅。","无溢流孔：与家用无水池不同，洁净室型号省去了溢流孔——这是细菌和霉菌臭名昭著的滋生地。","集成后挡板：深拉伸、高背设计，保护周围洁净室墙壁免受潮湿和喷雾影响。","管路系统由316L卫生管道制成"],"application_scenarios":["更衣进入/退出：在工作人员进入无菌生产区之前协助强制性手部擦洗协议。","实验室工具清洗：为冲洗高精度玻璃器皿或采样工具提供无污染区域。","无菌加工：用于药物配方区域中需要超纯水（WFI）的必要卫生步骤。","配方药房：在无菌药物制备前满足USP <797>手部卫生标准。","生物技术样品制备：冲洗细胞培养或基因组测序中使用的非反应性组件。","医疗器械清洗：手术植入物或器械在移至包装前的最后阶段冲洗。"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["拉丝处理","无缝焊接"]}}}'::jsonb)
WHERE id = 'bba65f04-0ad4-4540-8a2a-08cf96c056cc';

UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{zh}', '{"name":"移动存储箱","description":"带铰链盖的移动式不锈钢散装存储箱。非常适合在洁净室区域运输和存储散装原料或废物。","introduction":"用于散装物料的多功能移动存储。这款移动存储箱采用带铰链盖的重型不锈钢底盘，在运输过程中保护内容物免受污染。其光滑、无缝的内部确保符合标准的清洁，而坚固的脚轮则允许轻松操纵重负荷。非常适合通过分类区域移动原材料、废物或衣物。","features":["带手柄的不锈钢铰链盖","无缝内部结构，易于清洁","重型旋转脚轮","SUS 304 不锈钢","散装物品大容量","符合人体工程学的推手柄"],"application_scenarios":["洁净室物料运输","废物收集","液体转移"],"policies":{"warranty":"1年保修","shipping":"货运"},"specs":{"material":{"value":"304 不锈钢"},"process_technology":{"value":["TIG焊接","无缝焊接","双层覆层","全抛光工艺","拉丝处理"]}}}'::jsonb)
WHERE id = '7931b897-6ca1-4db4-95d7-90843c788385';

