UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{fr}', '{"name": "Bac de rangement mobile", "description": "Bac de stockage en vrac mobile en acier inoxydable avec couvercle à charnière. Idéal pour le transport et le stockage d''ingrédients en vrac ou de déchets dans les zones de salle blanche.", "introduction": "Stockage mobile polyvalent pour les matériaux en vrac. Ce bac de rangement mobile dispose d''un châssis robuste en acier inoxydable avec un couvercle à charnière, protégeant le contenu de la contamination pendant le transport. Son intérieur lisse et sans soudure assure un nettoyage conforme aux normes, tandis que les roulettes robustes permettent de manœuvrer facilement des charges lourdes. Parfait pour déplacer des matières premières, des déchets ou du linge à travers des zones classées.", "features": ["Couvercle à charnière en acier inoxydable avec poignée", "Construction intérieure sans soudure pour un nettoyage facile", "Roulettes pivotantes robustes", "Acier inoxydable SUS 304", "Grande capacité pour les articles en vrac", "Poignée de poussée pour un transport ergonomique"], "application_scenarios": ["Transport de matériel en salle blanche", "Collecte des déchets", "Transfert de liquide"], "specs": {"material": {"value": "Acier inoxydable 304"}}, "policies": {"warranty": "Garantie 1 an", "shipping": "Fret"}}'::jsonb)
