UPDATE products 
SET translations = jsonb_set(coalesce(translations, '{}'), '{fr}', '{"name": "Chariot élévateur de rack de lavage", "description": "Chariot de transfert de rack de lavage – Conçu pour transporter les racks de lavage et effectuer des tests d''efficacité de nettoyage hors ligne.", "introduction": "L''équipement est construit en acier inoxydable 316L et comporte des tubes sanitaires 316L avec des soudures orbitales. Il est équipé de colonnes de levage SKF, d''une finition de surface entièrement électropolie et est alimenté par une batterie 24V DC.", "features": ["Plage de levage : 580 mm – 980 mm", "Capacité de charge max. : 400 kg", "Batterie au lithium rechargeable", "Levage télécommandé", "Test d''efficacité de nettoyage hors ligne", "Finition électropolie", "Personnalisable pour des empreintes d''équipement spécifiques"], "application_scenarios": ["Transfert de rack de lavage", "Évaluation de l''efficacité du nettoyage des articles lavés"], "specs": {"material": {"value": "Acier inoxydable 304"}}, "policies": {"warranty": "Garantie 1 an", "shipping": "Fret"}}'::jsonb)
